$.fn.getSize = function () {
    var $wrap = $("<div />").appendTo($("body"));
    $wrap.css({
        "position": "absolute !important",
        "visibility": "hidden !important",
        "display": "block !important"
    });

    $clone = $(this).clone().appendTo($wrap);

    sizes = {
        "width": this.width(),
        "height": this.height()
    };

    $wrap.remove();

    return sizes;
};

